package week1.day1;

public class LearnIfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=0;
		
		if(num>0) {
			System.out.println("The number is postivie: "+num);
		}
		
		else if(num==0) {
			System.out.println("The nummber is zero: "+num);	
		}
		
		else  {
			System.out.println("The number is negative: "+num);
	    }
	}

}
