package week1.day2;

public class LearnJumpStmt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("Break statement");
		
		for (int i = 0; i < 6; i++) {
			
			if(i==3) {
				break;
			}
			System.out.println(i);
		}
System.out.println("Continue statement");

for (int i = 0; i < 6; i++) {
	
	if(i==3) {
		continue;
	}
	System.out.println(i);

	}
	}
}


