package week1.day2;

public class PrintOddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=20;
		
		for (int i = num; i >= num; i--) {
			
			if (i%2!=0) {
				
				System.out.println("Odd numbers are: "+i);
			}
			
		}

	}

}
